static void buf_lo_after_scan ( struct gfs2_jdesc * jd , int error , int pass ) { struct gfs2_inode * ip = GFS2_I ( jd -> jd_inode ) ; struct gfs2_sbd * sdp = GFS2_SB ( jd -> jd_inode ) ; if ( error ) { gfs2_inode_metasync ( ip -> i_gl , NULL ) ; return ; } if ( pass != 1 ) { return ; } gfs2_inode_metasync ( ip -> i_gl ) ; fs_info ( sdp , "jid=%u: Replayed %u of %u blocks\n" , jd -> jd_jid , jd -> jd_replayed_blocks , jd -> jd_found_blocks ) ; } 