char * * msDBFGetValueList ( DBFHandle dbffile , int record , int * itemindexes , int numitems ) { const char * value ; char * * values = NULL ; int i ; values = ( char * * ) malloc ( sizeof ( char * ) * numitems ) ; MS_CHECK_ALLOC ( values , sizeof ( char * ) * numitems , NULL ) ; for ( i = 0 ; i < numitems ; i ++ ) { value = msDBFReadStringAttribute ( dbffile , record , itemindexes [ i ] ) ; if ( value == NULL ) { free ( values ) ; return NULL ; } values [ i ] = msStrdup ( value ) ; } return ( values ) ; } 