static int crypto_aead_report ( struct sk_buff * skb , struct crypto_alg * alg ) { struct crypto_report_aead raead ; struct aead_alg * aead = container_of ( alg , aead_alg , base ) ; strscpy ( raead . type , "aead" , sizeof ( raead . type ) ) ; strscpy ( raead . geniv , "<none>" , sizeof ( raead . geniv ) ) ; raead . blocksize = alg -> cra_blocksize ; raead . maxauthsize = aead -> maxauthsize ; raead . ivsize = aead -> ivsize ; return nla_put ( skb , CRYPTOCFGA_REPORT_AEAD , sizeof ( raead ) , & raead ) ; } 