static bool validate_cmds_sorted ( const struct intel_engine_cs * engine , const struct drm_i915_cmd_table * cmd_tables , int cmd_table_count ) { int i ; bool ret = true ; for ( i = 0 ; i < cmd_table_count ; i ++ ) { const struct drm_i915_cmd_table * table = & cmd_tables [ i ] ; u32 previous = 0 ; int j ; for ( j = 0 ; j < table -> count ; j ++ ) { const struct drm_i915_cmd_descriptor * desc = & table -> table [ j ] ; u32 curr = desc -> cmd . value & desc -> cmd . mask ; if ( curr < previous ) { DRM_ERROR ( "CMD: %s [%d] command table not sorted: " "table=%d entry=%d cmd=0x%08X prev=0x%08X\n" , engine -> name , engine -> id , i , j , curr , previous ) ; ret = false ; } previous = curr ; } } return ret ; } 