static int mtdswap_choose_wl_tree ( struct mtdswap_dev * d ) { static unsigned int pick_cnt ; unsigned int i , idx = - 1 , wear , max ; struct rb_root * root ; max = 0 ; for ( i = 0 ; i <= MTDSWAP_DIRTY ; i ++ ) { root = & d -> trees [ i ] . root ; if ( root -> rb_node == NULL ) { continue ; } wear = d -> max_erase_count - MTDSWAP_ECNT_MIN ( root ) ; if ( wear > max ) { max = wear ; idx = i ; } } if ( max > MAX_ERASE_DIFF && pick_cnt >= mtdswap_wlfreq ( max ) - 1 ) { return idx ; } pick_cnt ++ ; return - 1 ; } 