int tegra_powergate_sequence_power_up ( unsigned int id , struct clk * clk , struct reset_control * rst ) { struct tegra_powergate pg ; int err ; pg . id = id ; pg . clks = & clk ; pg . num_clks = 1 ; pg . resets = & rst ; pg . num_resets = 1 ; err = tegra_powergate_power_up ( & pg , false ) ; if ( err ) { pr_err ( "failed to turn on partition %d: %d\n" , id , err ) ; } return err ; } 