void virTypedParamsRemoteFree ( struct _virTypedParameterRemote * remote_params_val , unsigned int remote_params_len ) { size_t i ; if ( ! remote_params_val ) { return ; } for ( i = 0 ; i < remote_params_len ; i ++ ) { g_free ( remote_params_val [ i ] . field ) ; if ( remote_params_val [ i ] . value . type == VIR_TYPED_PARAM_STRING ) { g_free ( remote_params_val [ i ] . value . remote_typed_param_value . s ) ; } } } 