static void cache_remove_all_class ( sysevent_handle_t * shp , uint32_t sub_id ) { int i ; class_lst_t * c_list ; subclass_lst_t * sc_list ; for ( i = 0 ; i < CLASS_HASH_SZ + 1 ; ++ i ) { c_list = SH_CLASS_HASH ( shp ) [ i ] ; while ( c_list != NULL ) { sc_list = c_list -> cl_subclass_list ; while ( sc_list != NULL ) { sc_list = sc_list -> sl_next ; } c_list = c_list -> cl_next ; } } } 