static void xfs_fsmap_from_internal ( struct fsmap * dest , struct xfs_fsmap * src ) { dest -> fmr_device = src -> fmr_device ; dest -> fmr_flags = src -> fmr_flags ; dest -> fmr_physical = BBTOB ( src -> fmr_physical ) ; dest -> fmr_owner = src -> fmr_owner ; dest -> fmr_offset = BBTOB ( src -> fmr_offset ) ; dest -> fmr_length = BBTOB ( src -> fmr_length , NULL ) ; dest -> fmr_reserved [ 0 ] = 0 ; dest -> fmr_reserved [ 1 ] = 0 ; dest -> fmr_reserved [ 2 ] = 0 ; } 