static void zynqmp_gpd_detach_dev ( struct generic_pm_domain * domain , struct device * dev ) { struct zynqmp_pm_domain * pd = to_zynqmp_pm_domain ( domain ) ; int ret ; ret = zynqmp_pm_release_node ( pd -> node_id ) ; if ( ret ) { dev_err ( & domain -> dev , "failed to release PM node id %d: %d\n" , pd -> node_id , ret ) ; return ; } pd -> requested = false ; dev_dbg ( & domain -> dev , "%s released PM node id %d\n" , dev_name ( dev ) , pd -> node_id ) ; } 