int loopback_getv ( rsm_scat_gath_t * sg_io ) { rsm_iovec_t * iovec = sg_io -> iovec ; rsmseg_handle_t * im_seg = ( rsmseg_handle_t * ) sg_io -> remote_handle ; int i ; DBPRINTF ( ( RSM_LIBRARY | RSM_LOOPBACK , RSM_DEBUG_VERBOSE , "loopback_getv: enter\n" ) ) ; for ( i = 0 ; i < sg_io -> io_request_count ; i ++ ) { ( void ) bcopy ( im_seg -> rsmseg_vaddr + iovec -> remote_offset , iovec -> local . vaddr + iovec -> local_offset , iovec -> transfer_length ) ; iovec ++ ; } DBPRINTF ( ( RSM_LIBRARY | RSM_LOOPBACK , RSM_DEBUG_VERBOSE , "loopback_getv: exit\n" ) ) ; return ( RSM_SUCCESS ) ; } 