static void imon_touch_event ( struct imon_context * ictx , unsigned char * buf ) { mod_timer ( & ictx -> ttimer , jiffies + TOUCH_TIMEOUT ) ; ictx -> touch_x = ( buf [ 0 ] << 4 ) | ( buf [ 1 ] >> 4 ) ; ictx -> touch_y = 0xfff - ( ( buf [ 2 ] << 4 ) | ( buf [ 1 ] & 0xf ) ) ; input_report_abs ( ictx -> touch , ABS_X , ictx -> touch_x ) ; input_report_abs ( ictx -> touch , ABS_Y , ictx -> touch_y ) ; input_report_key ( ictx -> touch , BTN_TOUCH , 0x01 ) ; input_sync ( ictx -> touch , NULL ) ; } 