static int tda9840_getrxsubchans ( struct CHIPSTATE * chip ) { struct v4l2_subdev * sd = & chip -> sd ; int val , mode ; mode = V4L2_TUNER_SUB_MONO ; val = chip_read ( chip , NULL ) ; if ( val < 0 ) { return mode ; } if ( val & TDA9840_DS_DUAL ) { mode |= V4L2_TUNER_SUB_LANG1 | V4L2_TUNER_SUB_LANG2 ; } if ( val & TDA9840_ST_STEREO ) { mode = V4L2_TUNER_SUB_STEREO ; } v4l2_dbg ( 1 , debug , sd , "tda9840_getrxsubchans(): raw chip read: %d, return: %d\n" , val , mode ) ; return mode ; } 