static int pp_check_ntb ( struct ntb_dev * ntb ) { u64 pmask ; if ( ntb_db_is_unsafe ( ntb ) ) { dev_dbg ( & ntb -> dev , "Doorbell is unsafe\n" ) ; } if ( ntb_spad_is_unsafe ( ntb ) ) { dev_dbg ( & ntb -> dev , "Scratchpad is unsafe\n" ) ; if ( ! unsafe ) { return - EINVAL ; } } pmask = GENMASK_ULL ( ntb_peer_port_count ( ntb ) , 0 ) ; if ( ( ntb_db_valid_mask ( ntb ) & pmask ) != pmask ) { dev_err ( & ntb -> dev , "Unsupported DB configuration\n" ) ; return - EINVAL ; } if ( ntb_spad_count ( ntb ) < 1 && ntb_msg_count ( ntb ) < 1 ) { dev_err ( & ntb -> dev , "Scratchpads and messages unsupported\n" ) ; return - EINVAL ; } if ( ntb_spad_count ( ntb ) < 1 ) { dev_dbg ( & ntb -> dev , "Scratchpads unsupported\n" ) ; } if ( ntb_msg_count ( ntb ) < 1 ) { dev_dbg ( & ntb -> dev , "Messages unsupported\n" ) ; } return 0 ; } 