static void device_woke_up ( struct hci_uart * hu ) { unsigned long flags , idle_delay ; struct qca_data * qca = hu -> priv ; struct sk_buff * skb = NULL ; BT_DBG ( "hu %p woke up" , hu ) ; spin_lock_irqsave ( & qca -> hci_ibs_lock , flags ) ; qca -> ibs_recv_wacks ++ ; switch ( qca -> tx_ibs_state ) { case HCI_IBS_TX_AWAKE : BT_DBG ( "Received HCI_IBS_WAKE_ACK in tx state %d" , qca -> tx_ibs_state ) ; break ; case HCI_IBS_TX_WAKING : while ( ( skb = skb_dequeue ( & qca -> tx_wait_q ) ) ) { skb_queue_tail ( & qca -> txq , skb ) ; } del_timer ( & qca -> wake_retrans_timer ) ; idle_delay = msecs_to_jiffies ( qca -> tx_idle_delay ) ; mod_timer ( & qca -> tx_idle_timer , jiffies + idle_delay ) ; qca -> tx_ibs_state = HCI_IBS_TX_AWAKE ; break ; case HCI_IBS_TX_ASLEEP : default : BT_ERR ( "Received HCI_IBS_WAKE_ACK in tx state %d" , qca -> tx_ibs_state ) ; break ; } spin_unlock_irqrestore ( & qca -> hci_ibs_lock , flags ) ; hci_uart_tx_wakeup ( hu , NULL ) ; } 