int __get_authtoken_attr ( char * name , pwu_repository_t * rep , attrlist * item ) { int repositories ; int i ; int res ; repositories = get_ns ( rep , PWU_READ ) ; if ( repositories == REP_ERANGE ) { repositories = REP_NSS ; } i = REP_FILES ; res = PWU_NOT_FOUND ; while ( ( i <= REP_LAST ) && ( res == PWU_NOT_FOUND ) ) { if ( repositories & i ) { res = rops [ i ] -> getattr ( name , item , rep ) ; } i <<= 1 ; } return ( res ) ; } 