static int yy_find_shift_action ( yyParser * pParser , int iLookAhead ) { int i ; int stateno = pParser -> yystack [ pParser -> yyidx ] . stateno ; i = yy_shift_ofst [ stateno ] ; if ( i == YY_SHIFT_USE_DFLT ) { return yy_default [ stateno ] ; } if ( iLookAhead == YYNOCODE ) { return YY_NO_ACTION ; } i += iLookAhead ; if ( i < 0 || i >= YY_SZ_ACTTAB || yy_lookahead [ i ] != iLookAhead ) { int iFallback ; if ( iLookAhead < sizeof ( yyFallback ) / sizeof ( yyFallback [ 0 ] ) && ( iFallback = yyFallback [ iLookAhead ] ) != 0 ) { if ( yyTraceFILE ) { fprintf ( yyTraceFILE , "%sFALLBACK %s =>%s\n" , yyTracePrompt , yyTokenName [ iLookAhead ] , yyTokenName [ iFallback ] ) ; } return yy_find_shift_action ( pParser , iFallback ) ; } return yy_default [ stateno ] ; } else { return yy_action [ i ] ; } } 