static int expand_preop ( struct expression * expr ) { int cost ; switch ( expr -> op ) { case '*' : return expand_dereference ( expr ) ; case '&' : return expand_addressof ( expr ) ; case SPECIAL_INCREMENT : case SPECIAL_DECREMENT : return expand_postop ( expr ) ; default : break ; } cost = expand_expression ( expr -> unop ) ; if ( simplify_float_preop ( expr ) ) { return 0 ; } return cost + 1 ; } 