static bool cred_cap_issubset ( const struct cred * set , const struct cred * subset ) { const struct user_namespace * set_ns = set -> user_ns ; const struct user_namespace * subset_ns = subset -> user_ns ; for ( ; subset_ns != & init_user_ns ; subset_ns = subset_ns -> parent ) { if ( ( set_ns == subset_ns -> parent ) && uid_eq ( subset_ns -> owner , set -> euid ) ) { return true ; } } return false ; } 