int snd_seq_oss_midi_check_exit_port ( int client , int port ) { struct seq_oss_midi * mdev ; unsigned long flags ; int index ; mdev = find_slot ( client , port ) ; if ( mdev ) { spin_lock_irqsave ( & register_lock , flags ) ; midi_devs [ mdev -> seq_device ] = NULL ; spin_unlock_irqrestore ( & register_lock , flags ) ; snd_use_lock_free ( & mdev -> use_lock ) ; snd_use_lock_sync ( & mdev -> use_lock ) ; snd_midi_event_free ( mdev -> coder ) ; } spin_lock_irqsave ( & register_lock , flags ) ; for ( index = max_midi_devs - 1 ; index >= 0 ; index -- ) { if ( midi_devs [ index ] ) { break ; } } max_midi_devs = index + 1 ; spin_unlock_irqrestore ( & register_lock , flags ) ; return 0 ; } 