static acpi_status acpi_find_extended_socket_device ( acpi_handle obj_handle , u32 level , void * context , void * * return_value ) { struct acpi_device * device ; bool * found = context ; device = acpi_fetch_acpi_dev ( obj_handle ) ; if ( ! device ) { return AE_OK ; } if ( strcmp ( acpi_device_hid ( device ) , ACPI_MOTHERBOARD_RESOURCE_HID ) ) { return AE_OK ; } if ( acpi_check_extended_socket_status ( device ) ) { return AE_OK ; } * found = true ; return AE_CTRL_TERMINATE ; } 