static bool_t xdrrec_putbytes ( XDR * xdrs , caddr_t addr , int32_t len ) { RECSTREAM * rstrm = ( RECSTREAM * ) ( xdrs -> x_private ) ; int current ; while ( len > 0 ) { current = rstrm -> out_boundry - rstrm -> out_finger ; current = ( len < current ) ?len : current ; bcopy ( addr , rstrm -> out_finger , current ) ; rstrm -> out_finger += current ; addr += current ; len -= current ; if ( rstrm -> out_finger == rstrm -> out_boundry ) { rstrm -> frag_sent = TRUE ; if ( ! flush_out ( rstrm , FALSE ) ) { return ( FALSE ) ; } } } return ( TRUE ) ; } 