static int mei_cl_device_probe ( struct device * dev ) { struct mei_cl_device * cldev ; struct mei_cl_driver * cldrv ; const struct mei_cl_device_id * id ; int ret ; cldev = to_mei_cl_device ( dev ) ; cldrv = to_mei_cl_driver ( dev -> driver , NULL ) ; if ( ! cldev ) { return 0 ; } if ( ! cldrv || ! cldrv -> probe ) { return - ENODEV ; } id = mei_cl_device_find ( cldev , cldrv ) ; if ( ! id ) { return - ENODEV ; } if ( ! mei_cl_bus_module_get ( cldev ) ) { dev_err ( & cldev -> dev , "get hw module failed" ) ; return - ENODEV ; } ret = cldrv -> probe ( cldev , id ) ; if ( ret ) { mei_cl_bus_module_put ( cldev ) ; return ret ; } __module_get ( THIS_MODULE ) ; return 0 ; } 