static int usb_dmac_desc_alloc ( struct usb_dmac_chan * chan , unsigned int sg_len , gfp_t gfp ) { struct usb_dmac_desc * desc ; unsigned long flags ; desc = kzalloc ( struct_size ( desc , sg , sg_len ) , gfp ) ; desc -> sg_allocated_len = sg_len ; INIT_LIST_HEAD ( & desc -> node ) ; spin_lock_irqsave ( & chan -> vc . lock , flags ) ; list_add_tail ( & desc -> node , & chan -> desc_freed ) ; spin_unlock_irqrestore ( & chan -> vc . lock , flags ) ; return 0 ; } 