static int stk3310_init ( struct iio_dev * indio_dev ) { int ret ; int chipid ; u8 state ; struct stk3310_data * data = iio_priv ( indio_dev ) ; struct i2c_client * client = data -> client ; ret = regmap_read ( data -> regmap , STK3310_REG_ID , & chipid ) ; if ( chipid != STK3310_CHIP_ID_VAL && chipid != STK3311_CHIP_ID_VAL ) { dev_err ( & client -> dev , "invalid chip id: 0x%x\n" , chipid ) ; return - ENODEV ; } state = STK3310_STATE_EN_ALS | STK3310_STATE_EN_PS ; ret = stk3310_set_state ( data , state ) ; if ( ret < 0 ) { dev_err ( & client -> dev , "failed to enable sensor" ) ; return ret ; } ret = regmap_field_write ( data -> reg_int_ps , STK3310_PSINT_EN ) ; if ( ret < 0 ) { dev_err ( & client -> dev , "failed to enable interrupts!\n" ) ; } return ret ; } 