int transport_set_option ( struct transport * transport , const char * name , const char * value ) { int git_reports = 1 , protocol_reports = 1 ; if ( transport -> smart_options ) { git_reports = set_git_option ( transport -> smart_options , name , value ) ; } if ( transport -> vtable -> set_option ) { protocol_reports = transport -> vtable -> set_option ( transport , name , value ) ; } if ( ( git_reports == - 1 ) || ( protocol_reports == - 1 ) ) { return - 1 ; } return 1 ; } 