static struct clk_hw * clk_register_stm32_timer_ker ( struct device * dev , const char * name , const char * parent_name , unsigned long flags , u8 dppre_shift , spinlock_t * lock ) { struct timer_ker * element ; struct clk_init_data init ; struct clk_hw * hw ; int err ; element = kmalloc ( sizeof ( * element ) , GFP_KERNEL ) ; if ( ! element ) { return ERR_PTR ( - ENOMEM ) ; } init . name = name ; init . ops = & timer_ker_ops ; init . flags = flags ; init . parent_names = & parent_name ; init . num_parents = 1 ; element -> hw . init = & init ; element -> lock = lock ; element -> dppre_shift = dppre_shift ; hw = & element -> hw ; err = clk_hw_register ( dev , hw ) ; if ( err ) { kfree ( element ) ; return ERR_PTR ( err ) ; } return hw ; } 