static irqreturn_t panfrost_gpu_irq_handler ( int irq , void * data ) { struct panfrost_device * pfdev = data ; u32 state = gpu_read ( pfdev , GPU_INT_STAT ) ; u32 fault_status = gpu_read ( pfdev , GPU_FAULT_STATUS ) ; if ( state & GPU_IRQ_MASK_ERROR ) { u64 address = ( u64 ) gpu_read ( pfdev , GPU_FAULT_ADDRESS_HI ) << 32 ; address |= gpu_read ( pfdev , GPU_FAULT_ADDRESS_LO ) ; dev_warn ( pfdev -> dev , "GPU Fault 0x%08x (%s) at 0x%016llx\n" , fault_status , panfrost_exception_name ( fault_status & 0xFF ) , address ) ; if ( state & GPU_IRQ_MULTIPLE_FAULT ) { dev_warn ( pfdev -> dev , "There were multiple GPU faults - some have not been reported\n" ) ; } gpu_write ( pfdev , GPU_INT_MASK , 0 ) ; } if ( state & GPU_IRQ_PERFCNT_SAMPLE_COMPLETED ) { panfrost_perfcnt_sample_done ( pfdev ) ; } if ( state & GPU_IRQ_CLEAN_CACHES_COMPLETED ) { panfrost_perfcnt_clean_cache_done ( pfdev ) ; } gpu_write ( pfdev , GPU_INT_CLEAR , state ) ; return IRQ_HANDLED ; } 