static int s3c_fb_probe_win ( struct s3c_fb * sfb , unsigned int win_no , struct s3c_fb_win_variant * variant , struct s3c_fb_win * * res ) { struct fb_var_screeninfo * var ; struct fb_videomode initmode ; struct s3c_fb_pd_win * windata ; struct s3c_fb_win * win ; struct fb_info * fbinfo ; int palette_size ; int ret ; dev_dbg ( sfb -> dev , "probing window %d, variant %p\n" , win_no , variant ) ; init_waitqueue_head ( & sfb -> vsync_info . wait ) ; palette_size = variant -> palette_sz * 4 ; fbinfo = framebuffer_alloc ( sizeof ( s3c_fb_win ) + palette_size * sizeof ( u32 ) , sfb -> dev ) ; if ( ! fbinfo ) { dev_err ( sfb -> dev , "failed to allocate framebuffer\n" ) ; return - ENOENT ; } windata = sfb -> pdata -> win [ win_no ] ; initmode = * sfb -> pdata -> vtiming ; WARN_ON ( windata -> max_bpp == 0 ) ; WARN_ON ( windata -> xres == 0 ) ; WARN_ON ( windata -> yres == 0 ) ; win = fbinfo -> par ; * res = win ; var = & fbinfo -> var ; win -> variant = * variant ; win -> fbinfo = fbinfo ; win -> parent = sfb ; win -> windata = windata ; win -> index = win_no ; win -> palette_buffer = ( u32 * ) ( win + 1 ) ; ret = s3c_fb_alloc_memory ( sfb , win ) ; if ( ret ) { dev_err ( sfb -> dev , "failed to allocate display memory\n" ) ; return ret ; } if ( win -> variant . palette_16bpp ) { win -> palette . r . offset = 11 ; win -> palette . r . length = 5 ; win -> palette . g . offset = 5 ; win -> palette . g . length = 6 ; win -> palette . b . offset = 0 ; win -> palette . b . length = 5 ; } else { win -> palette . r . offset = 16 ; win -> palette . r . length = 8 ; win -> palette . g . offset = 8 ; win -> palette . g . length = 8 ; win -> palette . b . offset = 0 ; win -> palette . b . length = 8 ; } initmode . xres = windata -> xres ; initmode . yres = windata -> yres ; fb_videomode_to_var ( & fbinfo -> var , & initmode ) ; fbinfo -> fix . type = FB_TYPE_PACKED_PIXELS ; fbinfo -> var . activate = FB_ACTIVATE_NOW ; fbinfo -> var . vmode = FB_VMODE_NONINTERLACED ; fbinfo -> var . bits_per_pixel = windata -> default_bpp ; fbinfo -> fbops = & s3c_fb_ops ; fbinfo -> flags = FBINFO_FLAG_DEFAULT ; fbinfo -> pseudo_palette = & win -> pseudo_palette ; ret = s3c_fb_check_var ( & fbinfo -> var , fbinfo ) ; if ( ret < 0 ) { dev_err ( sfb -> dev , "check_var failed on initial video params\n" ) ; return ret ; } ret = fb_alloc_cmap ( & fbinfo -> cmap , win -> variant . palette_sz , 1 ) ; if ( ret == 0 ) { fb_set_cmap ( & fbinfo -> cmap , fbinfo ) ; } else { dev_err ( sfb -> dev , "failed to allocate fb cmap\n" ) ; } s3c_fb_set_par ( fbinfo ) ; dev_dbg ( sfb -> dev , "about to register framebuffer\n" ) ; ret = register_framebuffer ( fbinfo ) ; if ( ret < 0 ) { dev_err ( sfb -> dev , "failed to register framebuffer\n" ) ; return ret ; } dev_info ( sfb -> dev , "window %d: fb %s\n" , win_no , fbinfo -> fix . id ) ; return 0 ; } 