static struct sched_class * t4_sched_class_lookup ( struct port_info * pi , const struct ch_sched_params * p ) { struct sched_table * s = pi -> sched_tbl ; struct sched_class * e , * end ; struct sched_class * found = NULL ; if ( ! p ) { end = & s -> tab [ s -> sched_size ] ; for ( e = & s -> tab [ 0 ] ; e != end ; ++ e ) { if ( e -> state == SCHED_STATE_UNUSED ) { found = e ; break ; } } } else { struct ch_sched_params info ; struct ch_sched_params tp ; memcpy ( & tp , p , sizeof ( tp ) ) ; tp . u . params . class = SCHED_CLS_NONE ; end = & s -> tab [ s -> sched_size ] ; for ( e = & s -> tab [ 0 ] ; e != end ; ++ e ) { memcpy ( & info , & e -> info , sizeof ( info ) ) ; info . u . params . class = SCHED_CLS_NONE ; if ( ( info . type == tp . type ) && ( ! memcmp ( & info . u . params , & tp . u . params , sizeof ( info . u . params ) ) ) ) { found = e ; break ; } } } return found ; } 