static inline int check_cd_factors ( const FFTXCodelet * cd , int len ) { int matches = 0 , any_flag = 0 ; for ( int i = 0 ; i < TX_MAX_FACTORS ; i ++ ) { int factor = cd -> factors [ i ] ; if ( factor == TX_FACTOR_ANY ) { any_flag = 1 ; matches ++ ; continue ; } if ( len <= 1 || ! factor ) { break ; } if ( factor == 2 ) { int bits_2 = ff_ctz ( len ) ; len >>= bits_2 ; matches ++ ; } else { int res = len % factor ; if ( res ) { continue ; } while ( ! res ) { len /= factor ; res = len % factor ; } matches ++ ; } } return ( cd -> nb_factors <= matches ) && ( any_flag || len == 1 ) ; } 