static int mask_all_events ( struct switchtec_dev * stdev , int eid ) { int idx ; int count = 0 ; if ( event_regs [ eid ] . map_reg == part_ev_reg ) { for ( idx = 0 ; idx < stdev -> partition_count ; idx ++ ) { count += mask_event ( stdev , eid , idx ) ; } } if ( event_regs [ eid ] . map_reg == pff_ev_reg ) { for ( idx = 0 ; idx < stdev -> pff_csr_count ; idx ++ ) { count += mask_event ( stdev , eid , idx ) ; } } else { count += mask_event ( stdev , eid , 0 ) ; } return count ; } 