static int same_device ( sd_device * a , sd_device * b ) { const char * a_val , * b_val ; int r ; assert ( a ) ; assert ( b ) ; r = sd_device_get_subsystem ( a , & a_val ) ; if ( r < 0 ) { return r ; } r = sd_device_get_subsystem ( b , & b_val ) ; if ( r < 0 ) { return r ; } r = sd_device_get_sysname ( a , & a_val ) ; if ( r < 0 ) { return r ; } r = sd_device_get_sysname ( b , & b_val ) ; if ( r < 0 ) { return r ; } return streq ( a_val , b_val ) ; } 