static void search_add_to_types ( RCore * c , RSignSearchMetrics * sm , RSignType t , const char * str , unsigned int * i ) { int count = * i ; r_return_if_fail ( count < sizeof ( sm -> stypes ) / sizeof ( RSignType ) - 1 ) ; if ( r_config_get_i ( c -> config , str ) ) { sm -> stypes [ count ++ ] = t ; sm -> stypes [ count ] = 0 ; * i = count ; } } 