static int process_command ( AVFilterContext * ctx , const char * cmd , const char * args , char * res , int res_len , int flags ) { AudioDRCContext * s = ctx -> priv ; char * old_expr_str = av_strdup ( s -> expr_str ) ; int ret ; ret = ff_filter_process_command ( ctx , cmd , args , res , res_len , flags ) ; if ( ret >= 0 && strcmp ( old_expr_str , s -> expr_str ) ) { ret = av_expr_parse ( & s -> expr , s -> expr_str , var_names , NULL , NULL , NULL , NULL , 0 , ctx ) ; } return ret ; } 