void mlxsw_sp_counter_free ( struct mlxsw_sp * mlxsw_sp , enum mlxsw_sp_counter_sub_pool_id sub_pool_id , unsigned int counter_index ) { struct mlxsw_sp_counter_pool * pool = mlxsw_sp -> counter_pool ; struct mlxsw_sp_counter_sub_pool * sub_pool ; int i ; sub_pool = & mlxsw_sp_counter_sub_pools [ sub_pool_id ] ; for ( i = 0 ; i < sub_pool -> entry_size ; i ++ ) { __clear_bit ( counter_index + i , pool -> usage ) ; } } 