static void rdt_dump_pids_data ( void ) { DEBUG ( RDT_PLUGIN ":  NAME     PIDs" ) ; char pids [ DATA_MAX_NAME_LEN ] ; for ( size_t i = 0 ; i < g_rdt -> num_ngroups ; ++ i ) { for ( size_t j = 0 ; j < g_rdt -> ngroups [ i ] . num_names ; ++ j ) { pids_list_t * list = g_rdt -> ngroups [ i ] . proc_pids [ j ] -> curr ; for ( size_t k = 0 ; k < list -> size ; k ++ ) { ssnprintf ( pids + strlen ( pids ) , sizeof ( pids ) - strlen ( pids ) - 1 , " %u" , list -> pids [ k ] ) ; } } DEBUG ( RDT_PLUGIN ":  [%s] %s" , g_rdt -> ngroups [ i ] . desc , pids ) ; } DEBUG ( RDT_PLUGIN ":  NAME    LLC[KB]   MBL[MB]MBR[MB]" ) ; for ( size_t i = 0 ; i < g_rdt -> num_ngroups ; i ++ ) { const struct pqos_event_values * pv = & g_rdt -> pngroups [ i ] -> values ; double llc = bytes_to_kb ( pv -> llc ) ; double mbr = bytes_to_mb ( pv -> mbm_remote_delta ) ; double mbl = bytes_to_mb ( pv -> mbm_local_delta ) ; DEBUG ( RDT_PLUGIN ":  [%s] %10.1f %10.1f %10.1f" , g_rdt -> ngroups [ i ] . desc , llc , mbl , mbr ) ; } } 