static bool bio_try_merge_hw_seg ( struct request_queue * q , struct bio * bio , struct page * page , unsigned len , unsigned offset , bool * same_page ) { struct bio_vec * bv = & bio -> bi_io_vec [ bio -> bi_vcnt - 1 ] ; unsigned long mask = queue_segment_boundary ( q ) ; phys_addr_t addr1 = page_to_phys ( bv -> bv_page ) + bv -> bv_offset ; phys_addr_t addr2 = page_to_phys ( page ) + offset + len - 1 ; if ( ( addr1 | mask ) != ( addr2 | mask ) ) { return false ; } return __bio_try_merge_page ( bio , page , len , offset , same_page ) ; } 