static char * usb_dump_desc ( char * start , char * end , struct usb_device * dev ) { int i ; start = usb_dump_device_descriptor ( start , end , & dev -> descriptor ) ; if ( start > end ) { return start ; } start = usb_dump_device_strings ( start , end , dev ) ; for ( i = 0 ; i < dev -> descriptor . bNumConfigurations ; i ++ ) { if ( start > end ) { return start ; } start = usb_dump_config ( dev -> speed , start , end , dev -> config + i , ( dev -> config + i ) == dev -> actconfig ) ; } return start ; } 