static void try_bulk_dequeue_skb ( struct Qdisc * q , struct sk_buff * skb , const struct netdev_queue * txq , int * packets ) { int bytelimit = qdisc_avail_bulklimit ( txq ) - skb -> len ; while ( bytelimit > 0 ) { struct sk_buff * nskb = q -> dequeue ( q ) ; if ( ! nskb ) { break ; } bytelimit -= nskb -> len ; skb -> next = nskb ; skb = nskb ; ( * packets ) ++ ; } skb_mark_not_on_list ( skb , NULL ) ; } 