static void rtadv_start_interface_events ( struct zebra_vrf * zvrf , struct zebra_if * zif ) { struct adv_if * adv_if = NULL ; if ( zif -> ifp -> ifindex == IFINDEX_INTERNAL ) { if ( IS_ZEBRA_DEBUG_EVENT ) { zlog_debug ( "%s(%s) has not configured an ifindex yet, delaying until we have one" , zif -> ifp -> name , zvrf -> vrf -> name ) ; } return ; } adv_if = adv_if_add ( zvrf , zif -> ifp -> name ) ; if_join_all_router ( zvrf -> rtadv . sock , zif -> ifp ) ; if ( adv_if_list_count ( & zvrf -> rtadv . adv_if ) == 1 ) { rtadv_event ( zvrf , RTADV_START , 0 ) ; } } 