static bool _job_part_valid ( job_record_t * job_ptr , part_record_t * part_ptr ) { part_record_t * avail_part_ptr ; ListIterator part_iterator ; bool rc = false ; if ( job_ptr -> part_ptr_list ) { part_iterator = list_iterator_create ( job_ptr -> part_ptr_list , NULL ) ; while ( ( avail_part_ptr = list_next ( part_iterator ) ) ) { if ( avail_part_ptr == part_ptr ) { rc = true ; break ; } } list_iterator_destroy ( part_iterator ) ; } if ( job_ptr -> part_ptr == part_ptr ) { rc = true ; } return rc ; } 