static bool test_r_crbtree_in_ascending_order_iteration2 ( void ) { RRBTree * tree = create_test_tree ( ) ; int s = 17 ; RRBNode * node = r_crbtree_find_node ( tree , & s , cmp_cb , NULL ) ; mu_assert_neq ( node , NULL , "Finding node failed" ) ; int * data = node -> data ; node = r_rbnode_next ( node , NULL ) ; bool t = true ; while ( node ) { int * next_data = node -> data ; t &= data [ 0 ] <= next_data [ 0 ] ; data = next_data ; node = r_rbnode_next ( node ) ; } mu_assert_true ( t , "iteration in ascending order failed" ) ; r_crbtree_free ( tree ) ; mu_end ; } 