int cmp_tracker ( const struct sm_state * a , const struct sm_state * b ) { int ret ; if ( a == b ) { return 0 ; } if ( ! a ) { return 1 ; } if ( a -> owner < b -> owner ) { return - 1 ; } if ( a -> owner > b -> owner ) { return 1 ; } ret = strcmp ( a -> name , b -> name ) ; if ( ret < 0 ) { return - 1 ; } if ( ret > 0 ) { return 1 ; } if ( ! b -> sym && a -> sym ) { return - 1 ; } if ( ! a -> sym && b -> sym ) { return 1 ; } if ( a -> sym < b -> sym ) { return - 1 ; } if ( a -> sym > b -> sym ) { return 1 ; } return 0 ; } 