static int eeepc_register_rfkill_notifier ( struct eeepc_laptop * eeepc , char * node ) { acpi_status status ; acpi_handle handle ; status = acpi_get_handle ( NULL , node , & handle ) ; status = acpi_install_notify_handler ( handle , ACPI_SYSTEM_NOTIFY , eeepc_rfkill_notify , eeepc ) ; if ( ACPI_FAILURE ( status ) ) { pr_warn ( "Failed to register notify on %s\n" , node ) ; } eeepc_rfkill_hotplug ( eeepc , handle ) ; return 0 ; } 