static void show_syncpts ( struct host1x * m , struct output * o ) { int i ; host1x_debug_output ( o , "---- syncpts ----\n" ) ; for ( i = 0 ; i < host1x_syncpt_nb_pts ( m ) ; i ++ ) { u32 max = host1x_syncpt_read_max ( m -> syncpt + i ) ; u32 min = host1x_syncpt_load ( m -> syncpt + i ) ; if ( ! min && ! max ) { continue ; } host1x_debug_output ( o , "id %u (%s) min %d max %d\n" , i , m -> syncpt [ i ] . name , min , max ) ; } for ( i = 0 ; i < host1x_syncpt_nb_bases ( m ) ; i ++ ) { u32 base_val ; base_val = host1x_syncpt_load_wait_base ( m -> syncpt + i ) ; if ( base_val ) { host1x_debug_output ( o , "waitbase id %u val %d\n" , i , base_val ) ; } } host1x_debug_output ( o , "\n" ) ; } 