const char * pgstat_get_wait_event_type ( uint32 wait_event_info ) { uint32 classId ; const char * event_type ; if ( wait_event_info == 0 ) { return in ; } classId = wait_event_info & 0xFF000000 ; switch ( classId ) { case PG_WAIT_LWLOCK : event_type = "LWLock" ; break ; case PG_WAIT_LOCK : event_type = "Lock" ; break ; case PG_WAIT_BUFFER_PIN : event_type = "BufferPin" ; break ; case PG_WAIT_ACTIVITY : event_type = "Activity" ; break ; case PG_WAIT_CLIENT : event_type = "Client" ; break ; case PG_WAIT_EXTENSION : event_type = "Extension" ; break ; case PG_WAIT_IPC : event_type = "IPC" ; break ; case PG_WAIT_TIMEOUT : event_type = "Timeout" ; break ; case PG_WAIT_IO : event_type = "IO" ; break ; default : event_type = "???" ; break ; } return event_type ; } 