static void keystone_get_stat_strings ( struct net_device * ndev , uint32_t stringset , uint8_t * data ) { struct netcp_intf * netcp = netdev_priv ( ndev ) ; struct gbe_intf * gbe_intf ; struct gbe_priv * gbe_dev ; int i ; gbe_intf = keystone_get_intf_data ( netcp ) ; gbe_dev = gbe_intf -> gbe_dev ; switch ( stringset ) { case ETH_SS_STATS : for ( i = 0 ; i < gbe_dev -> num_et_stats ; i ++ ) { memcpy ( data , gbe_dev -> et_stats [ i ] . desc , ETH_GSTRING_LEN ) ; data += ETH_GSTRING_LEN ; } break ; case ETH_SS_TEST : break ; } } 