u64 rtl8723e_get_desc ( struct ieee80211_hw * hw , u8 * pdesc8 , bool istx , u8 desc_name ) { u32 ret = 0 ; __le32 * pdesc = ( __le32 * ) pdesc8 ; if ( istx ) { switch ( desc_name ) { case HW_DESC_OWN : ret = get_tx_desc_own ( pdesc ) ; break ; case HW_DESC_TXBUFF_ADDR : ret = get_tx_desc_tx_buffer_address ( pdesc , NULL ) ; break ; default : WARN_ONCE ( true , "rtl8723ae: ERR txdesc :%d not processed\n" , desc_name ) ; break ; } } else { switch ( desc_name ) { case HW_DESC_OWN : ret = get_rx_desc_own ( pdesc ) ; break ; case HW_DESC_RXPKT_LEN : ret = get_rx_desc_pkt_len ( pdesc ) ; break ; case HW_DESC_RXBUFF_ADDR : ret = get_rx_desc_buff_addr ( pdesc ) ; break ; default : WARN_ONCE ( true , "rtl8723ae: ERR rxdesc :%d not processed\n" , desc_name ) ; break ; } } return ret ; } 