bool bin_update_shard_size ( unsigned bin_shard_sizes [ SC_NBINS ] , size_t start_size , size_t end_size , size_t nshards ) { if ( start_size > SC_SMALL_MAXCLASS ) { return false ; } if ( end_size > SC_SMALL_MAXCLASS ) { end_size = SC_SMALL_MAXCLASS ; } szind_t ind1 = sz_size2index_compute ( start_size ) ; szind_t ind2 = sz_size2index_compute ( end_size ) ; for ( unsigned i = ind1 ; i <= ind2 ; i ++ ) { bin_shard_sizes [ i ] = ( unsigned ) nshards ; } return false ; } 